		$('#mastermenu-dropdown li.menulevel-0 > a').on('click', function (evt) {
			var bigsub = $(this).next("div.mastermenu-bigsub");
			var href = $(this).attr('href');
			var baseClass = "mastermenu-bigsub",
				hideClass = "mastermenu-bigsub-hide";

			if (bigsub.length > 0 && bigsub.hasClass(baseClass) && !bigsub.is(":visible")) {				
				if (bigsub.hasClass(hideClass)) bigsub.removeClass(hideClass);
				if (_mastermenudd.mobileMode == "1") _mastermenudd.lastHref = href;

				bigsub.show();
				evt.preventDefault();
				return false;
			}
			else if (_mastermenudd.mobileMode == "1" && bigsub.length > 0) {				
				if (_mastermenudd.lastHref != href) {
					_mastermenudd.lastHref = href;
					evt.preventDefault();
					return false;
				}
			}
			return true;
		});

		$('div.mastermenu-bigsub a').on('click', function (evt) {
			$('#siteWrapper').css({ cursor: 'progress' });
			$(this).css({ 'text-decoration': 'underline' });
		});
	};

	$(function () {
		_mastermenudd.init();
	});

